import 'package:flutter/material.dart';
import 'package:yes_no_app/views/widgets/chat/her_message_bouble.dart';
import 'package:yes_no_app/views/widgets/chat/my_message_bouble.dart';

class ChatScreen extends StatelessWidget {
  const ChatScreen({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
          leading: const Padding(
            padding: EdgeInsets.all(2.0),
            child: CircleAvatar(
              backgroundImage: NetworkImage(
                  'data:image/jpeg;base64,'),
            ),
          ),
          title: const Text("Merlin <3")
          ),
          body: _ChatView(),
    );
  }
}

class _ChatView extends StatelessWidget {

  @override
  Widget build(BuildContext context) {
    //Safe Area hace que no toque el noch y en caso de los botones de regresar menu igual
    return SafeArea(
      child: Padding(
        padding: const EdgeInsets.symmetric(horizontal: 10),
        child: Column(
          children: [
            //Para Expanded decirle que tome todo el tamaÃ±o que sobra
            Expanded(child: ListView.builder(
              itemCount: 100,
              itemBuilder: (context, index) {
              return (index % 2 == 0 ) ?
              HerMessageBouble() :
              MyMessageBublle();
            },)
            ),
            Text('Mundo')
          ],
        ),
      ),
    );
  }
}
